(494, 1, 'RESOURCE_UPDATED', 49, '{\"updatedFields\": {\"tags\": [\"951\", \"953\", \"967\"], \"title\": \"U4 L1: What Do We Need and What Do We Want?\", \"status\": \"published\", \"type_id\": \"366\", \"grade_id\": \"628\", \"subject_id\": \"436\", \"description\": \"<ul><li><p>This presentation on <strong>Needs and Wants</strong> helps students understand the difference between essential needs like food, water, clothes, and shelter, and non-essential wants.</p></li></ul><ul><li><p>Includes interactive activities: Needs and Wants sorting, Draw a Smart Choice, and Gratitude Circle sharing.</p></li><li><p>Promotes responsibility, gratitude, and making wise choices in daily life.</p></li></ul><p></p>\"}}', '127.0.0.1', NULL, '2025-09-30 07:01:25'),
(495, 1, 'RESOURCE_UPDATED', 59, '{\"updatedFields\": {\"tags\": [\"951\", \"954\", \"966\", \"968\", \"990\", \"991\", \"992\"], \"title\": \"U3 L3: Celebrating Our Diversity\", \"status\": \"published\", \"type_id\": \"366\", \"grade_id\": \"629\", \"subject_id\": \"436\", \"description\": \"<ul><li><p>This presentation on <strong>Diversity</strong> introduces students to differences in food, languages, clothes, and celebrations, and explains how inclusive communities welcome and respect everyone.</p></li></ul><ul><li><p>Includes interactive activities: Cultural Celebration Collage, Diversity Tree, Tradition Show-and-Tell, and “Say Hello” in different languages.</p></li><li><p>Promotes kindness, respect, inclusion, and appreciation of cultural traditions.</p></li></ul><p></p>\"}}', '127.0.0.1', NULL, '2025-09-30 07:01:36'),
